class MCPClientManager:
    """Manages connections to multiple MCP servers."""

    def __init__(self):
        self.servers = {}
        self.tool_index = {}  # tool_name -> server

    async def connect_server(self, name: str, config: dict):
        """Connect to an MCP server."""
        if config['transport'] == 'stdio':
            client = await StdioMCPClient.connect(config['command'])
        else:
            client = await HttpMCPClient.connect(config['url'])

        self.servers[name] = client

        # Index tools for quick lookup
        tools = await client.list_tools()
        for tool in tools:
            self.tool_index[tool.name] = name

    async def call_tool(self, tool_name: str, args: dict):
        """Route tool call to appropriate server."""
        server_name = self.tool_index.get(tool_name)
        if not server_name:
            raise ToolNotFoundError(tool_name)

        return await self.servers[server_name].call_tool(tool_name, args)

    def generate_tool_filesystem(self, output_dir: str):
        """Generate filesystem representation for code execution mode."""
        for server_name, client in self.servers.items():
            server_dir = Path(output_dir) / server_name
            server_dir.mkdir(parents=True, exist_ok=True)

            for tool in client.tools:
                tool_file = server_dir / f"{tool.name}.ts"
                tool_file.write_text(generate_tool_wrapper(tool))
